mapper.insert:
	INSERT
	  INTO 
	        CLUB
	       (
	        CLUB_NO,
	        USER_NO,
		    CLUB_NAME,
		    CLUB_LIMIT,
			CLUB_CREATEDATE,
			CLUB_DISCRIPT,
			CLUB_IMG,
			CLUB_LOCATION,
			CLUB_STATUS
	       )
    VALUES 
		   (
		   SEQ_CNO.NEXTVAL
		  ,#{userNo}
		  ,#{clubName}
		  ,#{clubLimit}
		  ,SYSDATE
		  ,#{clubDiscript}
		  ,#{clubImg}
		  ,#{clubLocation}
		  ,'Y'
		   )
