mapper.insert:


INSERT 
  INTO 
       REVIEW
       (
        REVIEW_NO
       ,USER_NO
       ,REVIEW_STAR
       ,REVIEW_CONTENT
       ,REVIEW_STATUS
       ,BOOK_TITLE
       ,BOOK_IMAG
       ,BOOK_POINT
       ,BOOK_PUBLISHER
       ,BOOK_AUTHOR

       )
values
       (
        SEQ_RNO.NEXTVAL
       ,#{userNo}
       ,#{reviewStar}
       ,#{reviewContent}
       ,'N'
       ,#{bookTitle}
       ,#{bookImag} 
       ,10
       ,#{bookPublisher}
       ,#{bookAuthor}
       )

mapper.update:
		update
		       review
		   set
		        book_title = #{ bookTitle }
               ,book_author = #{ bookAuthor }
               ,book_publisher = #{ bookPublisher }
               ,book_imag = #{ bookImag }
               ,review_star = #{ reviewStar }
               ,review_content = #{ reviewContent }
		  where
		        review_no = #{ reviewNo }
